console.log("ğŸ“ === ENTERPRISE CONTENT PREPARATION (CAROUSEL) ===");
console.log("ğŸ“¥ Enterprise content creator output received");

// Clean and parse JSON output
const cleanJson = rawOutput.replace(/```json\n?/g, '').replace(/```\n?/g, '');
let contentData;

try {
    contentData = JSON.parse(cleanJson);
    console.log("âœ… Enterprise content data parsed successfully");
} catch (error) {
    console.error("âŒ Failed to parse enterprise content JSON:", error);
    throw new Error("Enterprise content parsing failed");
}

// Enterprise Content Validation for Carousel
const enterpriseValidation = {
    has_slides: !!(contentData.slides && Array.isArray(contentData.slides) && contentData.slides.length > 0),
    slides_have_content: contentData.slides?.every(s => s.title && s.subtitle) || false,
    has_caption_text: !!(contentData.caption?.text),
    has_hashtags: !!(contentData.caption?.hashtags),
    has_performance_prediction: !!(contentData.performance_prediction),
    slide_count_matches: (contentData.slides?.length || 0) === stateData.input_data.numberOfSlides,
    strategy_aligned: stateData.enterprise_config.content_strategy !== 'unknown'
};

const validationScore = Object.values(enterpriseValidation).filter(Boolean).length / Object.keys(enterpriseValidation).length;

console.log("ğŸ“‹ === ENTERPRISE CONTENT VALIDATION (CAROUSEL) ===");
Object.entries(enterpriseValidation).forEach(([check, passed]) => {
    console.log(`${passed ? 'âœ…' : 'âŒ'} ${check}: ${passed}`);
});
console.log("ğŸ¯ Enterprise Validation Score:", (validationScore * 100).toFixed(1) + '%');

// Enterprise Quality Gate
const qualityThreshold = stateData.enterprise_config.quality_threshold;
if (validationScore < qualityThreshold) {
    console.log("ğŸš¨ ENTERPRISE QUALITY GATE FAILED");
    throw new Error(`Enterprise quality standards not met: ${(validationScore * 100).toFixed(1)}%`);
}

console.log("ğŸ† ENTERPRISE QUALITY GATE PASSED!");

// Enterprise Content Package for Carousel
const enterpriseContentPackage = {
    // Carousel Data
    slides: contentData.slides,
    captionText: contentData.caption.text,
    hashtags: contentData.caption.hashtags,
    imageUrls: stateData.output_data.image_urls,
    execution_id: stateData.execution_id,

    // ENTERPRISE METADATA
    content_strategy: stateData.enterprise_config.content_strategy,
    priority_level: stateData.enterprise_config.priority_level,
    quality_threshold: stateData.enterprise_config.quality_threshold,
    validation_score: validationScore,
    enterprise_validated: true,

    // PERFORMANCE DATA
    performance_prediction: contentData.performance_prediction,

    // SOURCE DATA
    source_state_data: stateData,
    parsed_content_data: contentData
};

console.log("ğŸš€ ENTERPRISE CAROUSEL CONTENT PREPARATION SUCCESS!");

return [{ json: enterpriseContentPackage }];

