// Analyseer het type Telegram update - VEREENVOUDIGD VOOR 2 ROUTES
const update = $input.first().json;

// Check of het een callback query is
if (update.callback_query) {
  const callbackData = update.callback_query.data;

  // Check of het een state-based callback is (format: XXX_executionId_webhookId)
  const stateCallbackPattern = /^(AIP|RIP|MIP|AFP|RFP|MFP)_/;

  if (stateCallbackPattern.test(callbackData)) {
    return [{
      json: {
        type: 'state_callback',
        original: update
      }
    }];
  } else {
    // Andere soorten callbacks - behandel als marketing command
    return [{
      json: {
        type: 'marketing_command',
        original: update
      }
    }];
  }
}

// ALLE andere berichten (text, voice, photo, document, etc.) 
// gaan naar Marketing Director via marketing_command route
if (update.message) {
  return [{
    json: {
      type: 'marketing_command',
      original: update
    }
  }];
}

// Onbekend type - ook naar Marketing Director
return [{
  json: {
    type: 'marketing_command',
    original: update
  }
}];

// üî• ULTRA-SIMPLE DEBUG - WHAT IS COMING IN?
const input = $input.first().json;

console.log("üî• === ULTRA DEBUG ===");
console.log("üì• Raw input type:", typeof input);
console.log("üì• Raw input keys:", Object.keys(input || {}));
console.log("üì• Raw input JSON:", JSON.stringify(input, null, 2));

// Log specific properties we're looking for
console.log("üéØ input.update_id:", input.update_id);
console.log("üéØ input.message:", input.message);
console.log("üéØ input.body:", input.body);
console.log("üéØ input.query:", input.query);
console.log("üéØ input.headers:", input.headers);

// Try to find the actual data
let actualBody = null;
if (input.body) {
    actualBody = input.body;
    console.log("üì¶ Found data in .body:", JSON.stringify(actualBody, null, 2));
} else if (input.update_id) {
    actualBody = input;
    console.log("üì¶ Data is at root level");
} else {
    console.log("‚ùå No recognizable data found");
    actualBody = {};
}

// Simple classification for testing
const classification = {
    request_id: $execution.id,
    source: actualBody.update_id ? 'telegram_message' : 'unknown',
    type: actualBody.update_id ? 'content_request' : 'unknown',
    target_workflow: 'MarketingDirector',
    routing_strategy: 'debug_test',
    original_body: actualBody,
    priority_score: 50,
    source_confidence: actualBody.update_id ? 95 : 0,
    processing_latency_ms: 1,
    debug_info: {
        webhook_input: input,
        actual_body: actualBody,
        found_telegram_data: !!actualBody.update_id
    }
};

console.log("‚úÖ Classification result:", JSON.stringify(classification, null, 2));
console.log("üî• === DEBUG COMPLETE ===");

return [{json: classification}];
