// Clean and parse JSON output
const cleanJson = rawOutput.replace(/```json\n?/g, '').replace(/```\n?/g, '');
let scriptData;

try {
    scriptData = JSON.parse(cleanJson);
    console.log("âœ… Script data parsed successfully");
} catch (error) {
    console.error("âŒ Failed to parse script JSON:", error);
    throw new Error("Enterprise script parsing failed");
}

// Enterprise Script Validation
const enterpriseValidation = {
    has_scenes: !!(scriptData.scenes && Array.isArray(scriptData.scenes)),
    correct_scene_count: scriptData.scenes?.length === 4,
    all_scenes_complete: scriptData.scenes?.every(s => s.visual_description && s.text_overlay && s.scene_number && s.scene_purpose) || false,
    has_performance_prediction: !!(scriptData.performance_prediction),
    proper_scene_numbers: scriptData.scenes?.every(s => s.scene_number >= 1 && s.scene_number <= 4) || false,
    proper_durations: scriptData.scenes?.every(s => s.duration === 4) || false,
    text_overlay_length: scriptData.scenes?.every(s => s.text_overlay.split(' ').length <= 8) || false
};

const validationScore = Object.values(enterpriseValidation).filter(Boolean).length / Object.keys(enterpriseValidation).length;

console.log("ğŸ“‹ === ENTERPRISE SCRIPT VALIDATION ===");
Object.entries(enterpriseValidation).forEach(([check, passed]) => {
    console.log(`${passed ? 'âœ…' : 'âŒ'} ${check}: ${passed}`);
});
console.log("ğŸ¯ Script Validation Score:", (validationScore * 100).toFixed(1) + '%');

// Enterprise Quality Gate
const qualityThreshold = inputData.contentStrategy === 'campaign' ? 0.90 : 
                        inputData.contentStrategy === 'premium' ? 0.85 : 0.75;

if (validationScore < qualityThreshold) {
    console.log("ğŸš¨ ENTERPRISE SCRIPT QUALITY GATE FAILED");
    throw new Error(`Script quality standards not met: ${(validationScore * 100).toFixed(1)}%`);
}

console.log("ğŸ† ENTERPRISE SCRIPT QUALITY GATE PASSED!");

